const readline = require('readline');
const rl = readline.createInterface({
    input: process.stdin,
    output:process.stdout
})

function testi(Inputsana) {
    var sana = Inputsana.toLowerCase();
    var kaannetty = "";
    pituus = sana.length-1;
    for(let i=0; pituus>=i;pituus--){
        kaannetty+=sana[pituus];
    }
    console.log("Saatu sana: " + sana);
    console.log( "Kaannetty sana: " + kaannetty);

    return kaannetty === sana;
}


rl.question('Testaa onko palindromi:', vastaus => {
    var muuttuja = vastaus
    var palautus = testi(muuttuja);
    console.log(`Onko sanasi palindromi: ${palautus}`);
    rl.close()
})



